import React from 'react';
import { useForm, FormProvider, Controller } from "react-hook-form";
import { Input } from '../input';
import axios from 'axios';
import * as z from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import { useNavigate } from "react-router-dom";
//import './form.css'; // Make sure this file exists

// Schema for form validation
const formSchema = z.object({
    name: z.string()
      .min(3, 'Name must be at least 3 characters')
      .max(30, 'Name must be at most 30 characters'),
    email: z.string()
      .email('Invalid email address')
      .min(8, 'Email must be at least 8 characters')
      .max(30, 'Email must be at most 30 characters'),
    password: z.string()
      .min(5, 'Password must be at least 5 characters')
      .max(17, 'Password must be at most 17 characters'),
    confirmPassword: z.string()
      .min(5, 'Password must be at least 5 characters')
      .max(17, 'Password must be at most 17 characters'),
    profile: z.any()
});

export function Register() {
  const methods = useForm({
    resolver: zodResolver(formSchema),
    defaultValues: {
      name: "",
      email: "",
      password: "",
      confirmPassword: "",
      profile: null
    },
  });
  const navigate = useNavigate(); 
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState('');

  const onSubmit = async (values) => {
    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://127.0.0.1:5000/api/register', values, {
        withCredentials: true // Send cookies with the request
      });
      console.log(response.status);
      if (response.status === 204) {
        navigate('/');
      }
    } catch (error) {
      console.log(error);
      setError('Registration failed. Please check your details and try again.');
    } finally {
      setLoading(false);
    }
  };

  React.useEffect(() => {
    console.log(methods.getValues()); // Log form values on render
  }, [methods]);

  return (
    <div className="form-container">
      <div className="form-header">
        <h1>Register Now</h1>
      </div>
      <FormProvider {...methods}>
        <form onSubmit={methods.handleSubmit(onSubmit)} className="space-y-8">
          {/* Fields setup as previously */}
          {/* Remaining fields and Controller setups are the same */}
          
          {/* Button Section Updated */}
          <div className="form-button">
            <button type="submit" disabled={loading} className="submit-button">
              {loading ? <div className="loader"></div> : 'Register'}
            </button>
          </div>
        </form>
      </FormProvider>
      {error && <p className="error-message">{error}</p>}
    </div>
  );
}

export default Register;
